import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import NewsItem from "./NewsItem";

const categoryToImageMap = {
  General:
    "data:image/jpeg;base64,",
  Business:
    "data:image/jpeg;base64,",
  Entertainment:
    "data:image/jpeg;base64,",
  Health:
    "data:image/png;base64,",
  Sports:
    "data:image/jpeg;base64,",
  Technology:
    "data:image/jpeg;base64,",
};

const App = () => {
  const [category, setCategory] = useState("General");
  const [newsData, setNewsData] = useState([]);
  const [loading, setLoading] = useState(true);

  const onCategorySelect = (category) => {
    setCategory(category);
  };

  useEffect(() => {
    const fetchNews = async () => {
      const api_url = `https://newsapi.org/v2/top-headlines/sources?country=us&category=${category}&apiKey=${
        import.meta.env.VITE_API_KEY
      }`;
      try {
        setLoading(true);
        const response = await fetch(api_url);
        const data = await response.json();
        setNewsData(data.sources || []);
      } catch (error) {
        console.log(error);
      } finally {
        setLoading(false);
      }
    };
    fetchNews();
  }, [category]);

  // Prepare the news items with default images
  const preparedNews = newsData.map((item) => ({
    ...item,
    image: categoryToImageMap[category] || categoryToImageMap["General"],
  }));

  return (
    <div className="bg-gray-100 min-h-screen">
      <Navbar category={category} onCategorySelect={onCategorySelect} />
      <div className="container mx-auto py-8 px-4">
        {loading && (
          <div className="flex justify-center items-center py-20">
            <div className="loader ease-linear rounded-full border-4 border-t-4 border-gray-300 h-12 w-12"></div>
            <p className="ml-4 text-gray-600 font-semibold">Loading...</p>
          </div>
        )}
        {!loading && preparedNews.length > 0 && (
          <div className="flex flex-col gap-4">
            <h1 className="font-bold text-center text-red-500 text-5xl">
              Latest {category} News
            </h1>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-4">
              {preparedNews.map((item) => (
                <NewsItem
                  key={item.id}
                  image={item.image}
                  title={item.name}
                  description={
                    item.description
                      ? item.description.slice(0, 50) + "..."
                      : ""
                  }
                  url={item.url}
                />
              ))}
            </div>
          </div>
        )}
        {!loading && preparedNews.length === 0 && (
          <p className="text-center text-gray-500 mt-10">No news available</p>
        )}
      </div>
    </div>
  );
};

export default App;
